mapper.select:
SELECT id, name, avatar, star FROM customer WHERE status = #{status}
AND name LIKE CONCAT('%', #{name}, '%')
AND user_id = #{userId} ORDER BY star DESC

SELECT id, title, `desc`, thumb, media, type, create_time createTime FROM customer_media WHERE customer_id = #{id}
AND type = #{type}
ORDER BY create_time
LIMIT #{page}, #{size}

mapper.insert:
INSERT INTO customer_media (customer_id, media, title, `desc`, type, thumb)
VALUES
    ( #{id}, #{media}, #{title}, #{desc}, #{type}, #{thumb});
